import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Navbar({ username }) {
  const [menuOpen, setMenuOpen] = useState(false);

  const logout = () => {
    localStorage.removeItem('token');
    window.location.reload();
  };

  return (
    <nav className="top-nav">
      <div className="nav-container flex flex-col sm:flex-row sm:items-center sm:justify-between px-4 py-3">
        
        {/* Logo Area */}
        <div className="logo-area flex items-center gap-3 justify-between sm:justify-start w-full sm:w-auto">
          <div className="logo-circle">N</div>
          <h1 className="site-title">Luna’s Nikki Blog</h1>

          {/* Hamburger button (mobile only) */}
          <button
            onClick={() => setMenuOpen(!menuOpen)}
            className="sm:hidden text-2xl text-gray-700"
            aria-label="Toggle menu"
          >
            ☰
          </button>
        </div>

        {/* Navigation Links */}
        <div
          className={`nav-links flex-col sm:flex sm:flex-row gap-3 sm:gap-6 mt-3 sm:mt-0 ${
            menuOpen ? 'flex' : 'hidden'
          } sm:flex`}
        >
          <Link to="/">Home</Link>
          {username && <Link to="/create">Create</Link>}
          <Link to="/tags/sweet">Tags</Link>

          {username ? (
            <>
              <span className="text-sm text-gray-700">Logged in as: <strong>{username}</strong></span>
              <button
                onClick={logout}
                className="text-pink-600 text-sm border border-pink-300 px-2 py-1 rounded"
              >
                Logout
              </button>
            </>
          ) : (
            <Link to="/login">Login</Link>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
