import React from 'react';
import { Link } from 'react-router-dom';

function Layout({ children }) {
  return (
    <div className="min-h-screen flex flex-col" style={{ backgroundColor: '#fce4ec', color: '#4a4a4a' }}>
      {/* Header */}
      <header style={{ backgroundColor: '#eeb2b2' }} className="text-white shadow-md">
        <div className="container mx-auto px-4 py-6 flex justify-between items-center">
          <h1 className="text-3xl font-bold">Nikki Blog</h1>
          <nav>
            <ul className="flex gap-6 text-lg">
              <li><Link to="/" className="hover:underline">Home</Link></li>
              <li><Link to="/create" className="hover:underline">Create</Link></li>
              <li><Link to="/tags" className="hover:underline">Tags</Link></li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-grow container mx-auto px-4 py-8">
        {children}
      </main>

      {/* Footer */}
      <footer style={{ backgroundColor: '#dceeff' }} className="text-slate-800 py-6 mt-12">
        <div className="container mx-auto text-center text-sm">
          <p>&copy; {new Date().getFullYear()} Nikki Blog. All rights reserved.</p>
          <p className="mt-2">Crafted with ðŸ’• in pastel pixels</p>
        </div>
      </footer>
    </div>
  );
}

export default Layout;
