import React from 'react';
import { Link } from 'react-router-dom';

function Layout({ children }) {
  return (
    <div className="min-h-screen flex flex-col" style={{ backgroundColor: '#fce4ec', color: '#4a4a4a' }}>
      {/* Header */}
      
<header className="bg-white shadow-md sticky top-0 z-50">
  <div className="max-w-6xl mx-auto px-4 py-4 flex justify-between items-center">
    
    {/* Site logo/title */}
    <div className="flex items-center space-x-2">
      {/* Optional: Replace with logo image later */}
      <div className="w-10 h-10 bg-pink-300 rounded-full flex items-center justify-center text-white text-xl font-bold">N</div>
      <h1 className="text-xl font-bold text-pink-700">Nikki Blog</h1>
    </div>

    {/* Horizontal navigation */}
    <nav>
      <ul className="flex space-x-6 text-gray-700 font-medium text-base">
        <li>
          <Link to="/" className="hover:text-pink-600 transition duration-150">Home</Link>
        </li>
        <li>
          <Link to="/create" className="hover:text-pink-600 transition duration-150">Create</Link>
        </li>
        <li>
          <Link to="/tags" className="hover:text-pink-600 transition duration-150">Tags</Link>
        </li>
      </ul>
    </nav>
  </div>
</header>

      {/* Main Content */}
      <main className="flex-grow container mx-auto px-4 py-8">
        {children}
      </main>

      {/* Footer */}
      <footer style={{ backgroundColor: '#dceeff' }} className="text-slate-800 py-6 mt-12">
        <div className="container mx-auto text-center text-sm">
          <p>&copy; {new Date().getFullYear()} Nikki Blog. All rights reserved.</p>
          <p className="mt-2">Crafted with ðŸ’• in pastel pixels</p>
        </div>
      </footer>
    </div>
  );
}

export default Layout;
