import React from 'react';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; 
import Axios from 'axios'; 
import TagList from '../components/TagList';
import { Link } from 'react-router-dom';
import Pagination from '../components/Pagination'; // Import the Pagination component


function TagPage() {
    const { tag } = useParams(); // Get the tag from the URL
    const [posts, setPosts] = useState([]); // State to hold posts with the specified tag
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);

    useEffect(() => {
        Axios.get(`http://localhost:5000/api/posts/tag/${tag}?page=${currentPage}&limit=5`)
          .then(res => {
            setPosts(res.data.posts);
            setTotalPages(res.data.pages);
          })
          .catch(err => {
            console.error('Error fetching posts by tag:', err);
            setPosts([]);
          });
      }, [tag, currentPage]); 

    const getThumbnailFromContent = (markdown) => {
        const match = markdown.match(/!\[.*?\]\((.*?)\)/);
        return match ? match[1] : 'https://via.placeholder.com/300x200?text=No+Image';
      };
    
      const stripImagesAndMarkdown = (markdown) => {
        const noImages = markdown.replace(/!\[.*?\]\(.*?\)/g, '');
        return noImages.replace(/[#_*`>]/g, '').substring(0, 160);
      };
    
      const formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString('en-US', {
          year: 'numeric',
          month: 'short',
          day: 'numeric',
        });
      };

    return (
      <div className="max-w-4xl mx-auto px-4 py-8">
      <h1 className="text-center text-2xl mb-6">
        Posts tagged with <span className="text-pink-500">#{tag}</span>
      </h1>

      {posts.length === 0 ? (
        <p style={{ textAlign: 'center', color: '#999' }}>No posts found for this tag.</p>
      ) : (
        posts.map(post => {
          const thumbnail = getThumbnailFromContent(post.content);

          return (
            <div key={post._id} className="flex flex-col md:flex-row bg-white shadow-sm border border-gray-200 rounded-lg overflow-hidden mb-6">
            <img
              src={thumbnail}
              alt="Post thumbnail"
              className="w-full md:w-52 h-48 object-cover"
            />
          
            <div className="p-4 flex-1">
              <Link to={`/posts/${post._id}`} className="no-underline text-gray-800 hover:text-blue-600">
                <h2 className="text-lg font-semibold mb-2">{post.title}</h2>
              </Link>
          
              <p className="text-sm text-gray-500 mb-3">
                {formatDate(post.createdAt)}
              </p>
          
              <p className="text-gray-700 mb-4">
                {stripImagesAndMarkdown(post.content)}...
              </p>
          
              <TagList tags={post.tags} />
          
              <Link
                to={`/posts/${post._id}`}
                className="inline-block mt-4 px-4 py-2 bg-blue-500 text-white text-sm rounded hover:bg-blue-600 transition"
              >
                Read More â†’
              </Link>
            </div>
          </div>

            
          );
        },
        <Pagination
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
            />
      ))}
    </div>
  );
}

export default TagPage;
