import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Home from './pages/Home';
import CreatePost from './pages/CreatePost';
import PostPage from './pages/PostPage';
import EditPost from './pages/EditPost';
import TagPage from './pages/TagPage';
import Layout from './components/Layout';
import LoginPage from './pages/LoginPage';
import { useAuth } from './context/AuthContext';
import About from './pages/About';

function App() {
  const { username, logout } = useAuth();

  return (
    <Router>
      <nav className="bg-white shadow-sm sticky top-0 z-50">
  <div className="max-w-6xl mx-auto px-4 py-3 flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3 sm:gap-0">

    {/* Logo Area */}
    <div className="flex items-center gap-3">
      <div className="w-10 h-10 bg-pink-300 text-white rounded-full flex items-center justify-center font-bold text-lg">N</div>
      <h1 className="text-xl font-semibold text-rose-600">Luna's Nikki Blog</h1>
    </div>

    {/* Navigation Links */}
    <div className="flex flex-col sm:flex-row sm:items-center gap-2 sm:gap-6 text-sm sm:text-base">
      <Link to="/" className="text-gray-700 hover:text-rose-500">Home</Link>

      {username && (
        <Link to="/create" className="text-gray-700 hover:text-rose-500">Create</Link>
      )}

      {/* Tag Dropdown (basic, not interactive) */}
      <div className="relative group">
        <span className="text-gray-700 hover:text-rose-500 cursor-pointer">Tags ▾</span>
        <div className="absolute bg-white border border-gray-200 rounded shadow-md mt-2 p-2 hidden group-hover:block z-10">
          <Link to="/tags/sweet" className="block px-3 py-1 hover:bg-pink-100 rounded text-gray-700">#sweet</Link>
          <Link to="/tags/elegant" className="block px-3 py-1 hover:bg-yellow-100 rounded text-gray-700">#elegant</Link>
          <Link to="/tags/fresh" className="block px-3 py-1 hover:bg-green-100 rounded text-gray-700">#fresh</Link>
          <Link to="/tags/sexy" className="block px-3 py-1 hover:bg-purple-100 rounded text-gray-700">#sexy</Link>
          <Link to="/tags/cool" className="block px-3 py-1 hover:bg-blue-100 rounded text-gray-700">#cool</Link>
        </div>
      </div>

      {/* Auth section */}
      {username ? (
        <>
          <span className="text-gray-600">Logged in as: <strong>{username}</strong></span>
          <button
            onClick={() => {
              localStorage.removeItem('token');
              window.location.reload();
            }}
            className="text-pink-600 underline text-sm"
          >
            Logout
          </button>
        </>
      ) : (
        <Link to="/login" className="text-gray-700 hover:text-rose-500">Login</Link>
      )}
    </div>
  </div>
</nav>
      <Routes>
        <Route path="/" element={<Layout><Home /></Layout>} />
        <Route path="/about" element={<Layout><About /></Layout>} />
        <Route path="/create" element={<Layout><CreatePost /></Layout>} />
        <Route path="/posts/:id" element={<Layout><PostPage /></Layout>} />
        <Route path="/posts/edit/:id" element={<Layout><EditPost /></Layout>} />
        <Route path="/tags/:tag" element={<Layout><TagPage /></Layout>} />
        <Route path="/login" element={<LoginPage />} />
      </Routes>
    </Router>
  );
}

export default App;
