import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Home from './pages/Home';
import CreatePost from './pages/CreatePost';
import PostPage from './pages/PostPage';
import EditPost from './pages/EditPost';
import TagPage from './pages/TagPage';
import Layout from './components/Layout';

function App() {
  return (
    <Router>
      <nav className="bg-white shadow-md sticky top-0 z-50">
  <div className="max-w-6xl mx-auto px-4 py-4 flex justify-between items-center">

    {/* Site Title or Logo */}
    <div className="flex items-center space-x-2">
      <div className="w-10 h-10 bg-pink-300 rounded-full flex items-center justify-center text-white text-xl font-bold">N</div>
      <h1 className="text-xl font-bold text-pink-700">Nikki Blog</h1>
    </div>

    {/* Navigation Menu */}
    <div className="flex space-x-6 text-gray-700 font-medium text-base">
      <Link to="/" className="hover:text-pink-600 transition">Home</Link>
      <Link to="/create" className="hover:text-pink-600 transition">Create</Link>
      <Link to="/tags" className="hover:text-pink-600 transition">Tags</Link>
    </div>
  </div>
</nav>
      <Routes>
        <Route path="/" element={<Layout><Home /></Layout>} />
        <Route path="/create" element={<Layout><CreatePost /></Layout>} />
        <Route path="/posts/:id" element={<Layout><PostPage /></Layout>} />
        <Route path="/posts/edit/:id" element={<Layout><EditPost /></Layout>} />
        <Route path="/tags/:tag" element={<Layout><TagPage /></Layout>} />
      </Routes>
    </Router>
  );
}

export default App;
