import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import ReactMarkdown from 'react-markdown';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

function PostPage() {
    const { id } = useParams(); // Get postID from URL
    const { post, setPost } = useState(null);
    const navigate = useNavigate(); // For navigation after deletion
    useEffect(() => {
        Axios.get(`http://localhost:5000/api/posts/${id}`)
        .then(res => setPost(res.data))
        .catch(err => {
        console.error('Error loading post:', err);
        alert('Failed to load post. Check console.');
      });
    }, [id]);

    // delete post
    const handleDelete = async () => {
        const confirmDelete = window.confirm('Are you sure you want to delete this post?');
        if (!confirmDelete) return;
        try {
            await Axios.delete(`http://localhost:5000/api/posts/${id}`);
            navigate('/'); // go back to homepage after deletion
          } catch (error) {
            console.error('Failed to delete post:', error);
          }
    }

    if (!post) return <div>Loading..</div>;

    return (
        <div className="container">
            <h1>{post.title}</h1>
            {/* Render the post content as Markdown */}
            <div className="post-content" style={{ marginTop: '2rem' }}>
                <ReactMarkdown>{post.content}</ReactMarkdown>
            </div>
            <div style={{ marginTop: '1rem' }}>
                {post.tags.map((tag, index) => (
                    <span
                        key={index}
                        style={{
                            background: '#eee',
                            borderRadius: '5px',
                            padding: '5px 10px',
                            marginRight: '5px',
                            fontSize: '0.9rem',
                        }}
                    >
                        #{tag}
                    </span>
                ))}
            </div>
            <button
                onClick={handleDelete}
                style={{
                    marginTop: '2rem',
                    padding: '0.5rem 1rem',
                    background: '#ff4d4d',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer'
                }}
            >
                Delete Post
            </button>
        </div>
    );
}
export default PostPage;
