import React, { useState } from 'react';
import Axios from 'axios';
import ReactMarkdown from 'react-markdown';
import ReactMde from 'react-mde';
import 'react-mde/lib/styles/css/react-mde-all.css'
import { useNavigate } from 'react-router-dom';

function CreatePost() {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [tags, setTags] = useState('');
  const [selectedTab, setSelectedTab] = useState('write');
  const [success, setSuccess] = useState(false);
  const [imageFiles, setImageFiles] = useState([]);

  const handleImagesChange = (e) => {
    setImageFiles(Array.from(e.target.files)); // store multiple images
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSuccess(false);

    let finalContent = content;

    try {
      if (imageFiles.length > 0) {
        for (const file of imageFiles) {
          const formData = new FormData();
          formData.append('image', file);

          const res = await Axios.post('http://localhost:5000/api/posts/upload', formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
          });

          const imageUrl = `http://localhost:5000/uploads/${res.data.filename}`;
          finalContent += `\n\n![Uploaded Image](${imageUrl})\n`;
        }
      }

      await Axios.post('http://localhost:5000/api/posts', {
        title,
        content: finalContent,
        tags
      });

      setSuccess(true);
      navigate('/');
    } catch (error) {
      console.error('Error creating post:', error);
    }
  };

  return (
    <div className="container" style={{ maxWidth: '800px', margin: '0 auto', padding: '1rem' }}>
      <h1>Create a New Post</h1>

      <form onSubmit={handleSubmit}>
        {/* Title */}
        <div style={{ marginBottom: '1rem' }}>
          <input
            type="text"
            placeholder="Post Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{ width: '100%', padding: '0.5rem' }}
          />
        </div>

        {/* Markdown Editor */}
        <div style={{ marginBottom: '1rem' }}>
          <ReactMde
            value={content}
            onChange={setContent}
            selectedTab={selectedTab}
            onTabChange={setSelectedTab}
            generateMarkdownPreview={markdown =>
              Promise.resolve(<ReactMarkdown>{markdown}</ReactMarkdown>)
            }
          />
        </div>

        {/* Tags */}
        <div style={{ marginBottom: '1rem' }}>
          <input
            type="text"
            placeholder="Tags (comma separated)"
            value={tags}
            onChange={(e) => setTags(e.target.value)}
            style={{ width: '100%', padding: '0.5rem' }}
          />
        </div>

        {/* Multiple image file input */}
        <div style={{ marginBottom: '1rem' }}>
          <label>Upload one or more images</label><br />
          <input type="file" multiple accept="image/*" onChange={handleImagesChange} />
        </div>

        <button
          type="submit"
          style={{
            padding: '0.7rem 1.5rem',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          Create Post
        </button>
      </form>
    </div>
  );
}

export default CreatePost;
