import React from 'react';
import { Link } from 'react-router-dom';

function Layout({ children }) {
  return (
    <div className="min-h-screen flex flex-col" style={{ backgroundColor: '#fce4ec', color: '#4a4a4a' }}>
      {/* Header */}
      <header className="bg-white shadow-md">
        <div className="container mx-auto flex justify-between items-center py-4 px-6">
            {/* Logo area */}
            <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-pink-200 rounded-full flex items-center justify-center text-pink-700 font-bold text-xl">
                N
            </div>
            <h1 className="text-2xl font-semibold text-pink-800">Nikki Blog</h1>
            </div>

            {/* Navigation menu */}
            <nav>
            <ul className="flex gap-6 text-lg font-medium text-slate-700">
                <li><Link to="/" className="hover:text-pink-500 transition">Home</Link></li>
                <li><Link to="/create" className="hover:text-pink-500 transition">Create</Link></li>
                <li><Link to="/tags" className="hover:text-pink-500 transition">Tags</Link></li>
            </ul>
            </nav>
        </div>
        </header>

      {/* Main Content */}
      <main className="flex-grow container mx-auto px-4 py-8">
        {children}
      </main>

      {/* Footer */}
      <footer style={{ backgroundColor: '#dceeff' }} className="text-slate-800 py-6 mt-12">
        <div className="container mx-auto text-center text-sm">
          <p>&copy; {new Date().getFullYear()} Nikki Blog. All rights reserved.</p>
          <p className="mt-2">Crafted with ðŸ’• in pastel pixels</p>
        </div>
      </footer>
    </div>
  );
}

export default Layout;
