import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Navbar({ username }) {
  const [menuOpen, setMenuOpen] = useState(false);

  const logout = () => {
    localStorage.removeItem('token');
    window.location.reload();
  };

  return (
    <nav className="bg-white shadow-sm sticky top-0 z-50">
      <div className="max-w-6xl mx-auto px-4 py-3 flex items-center justify-between">
        {/* Logo */}
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-pink-300 text-white rounded-full flex items-center justify-center font-bold text-lg">N</div>
          <h1 className="text-xl font-semibold text-rose-600">Luna's Nikki Blog</h1>
        </div>

        {/* Hamburger button (mobile) */}
        <button
          onClick={() => setMenuOpen(!menuOpen)}
          className="sm:hidden text-gray-600 focus:outline-none"
        >
          â˜°
        </button>

        {/* Desktop Menu */}
        <div className="hidden sm:flex gap-6 items-center text-sm">
          <Link to="/" className="text-gray-700 hover:text-pink-500">Home</Link>
          {username && <Link to="/create" className="text-gray-700 hover:text-pink-500">Create</Link>}
          <Link to="/tags/sweet" className="text-gray-700 hover:text-pink-500">Tags</Link>
          {username ? (
            <>
              <span className="text-gray-600">Logged in as: <strong>{username}</strong></span>
              <button onClick={logout} className="text-pink-600 underline text-sm">Logout</button>
            </>
          ) : (
            <Link to="/login" className="text-gray-700 hover:text-pink-500">Login</Link>
          )}
        </div>
      </div>

      {/* Mobile Menu */}
      {menuOpen && (
        <div className="sm:hidden flex flex-col gap-3 px-6 pb-4 text-sm">
          <Link to="/" className="text-gray-700 hover:text-pink-500">Home</Link>
          {username && <Link to="/create" className="text-gray-700 hover:text-pink-500">Create</Link>}
          <Link to="/tags/sweet" className="text-gray-700 hover:text-pink-500">Tags</Link>
          {username ? (
            <>
              <span className="text-gray-600">Logged in as: <strong>{username}</strong></span>
              <button onClick={logout} className="text-pink-600 underline text-sm">Logout</button>
            </>
          ) : (
            <Link to="/login" className="text-gray-700 hover:text-pink-500">Login</Link>
          )}
        </div>
      )}
    </nav>
  );
}

export default Navbar;
